import { connect } from "react-redux";
import { React, useEffect, useRef } from "react";
import style from "../scss/location-map-show.module.scss";

import Map from "./Map";
import { APIKey, initialLocation, mapStyle } from "../../config/ConfigurationData";
import { Loader } from "@googlemaps/js-api-loader";

const google = window.google;

//Loading google map using googlemaps js-api-loader
const GoogleMapAPI = new Loader({
  apiKey: APIKey,
  language: "English",
});

//Containers for instances created by google built-in constructors
let GoogleMap, Marker, Geocoder, MyInfoWindow;

function LocationMapShow(props) {
  const mapRef = useRef(null);

  useEffect(() => {
    if (!google) return; //do dothing when window google undefined

    const map = new google.maps.Map(mapRef.current, {
      center: { lat: 43.6532, lng: -79.3832 }, //default center Toronto
      zoom: 8,
    });

    if (props.list.length > 0) {
      props.list.map((userLocationObj, index) => {
        const location = { lat: userLocationObj.lat, lng: userLocationObj.lng };

        if (index === props.list.length - 1) {
          //Center map on latest user location
          map.setCenter(location);
        }

        return new google.maps.Marker({
          position: location,
          map,
        });
      });
    }
  }, [props.list]);

  return (
    <div className={style["location-map-container"]}>
      <div ref={mapRef} className={style["location-map"]}></div>
    </div>
  );
}

export default connect((state) => {
  const { list } = state;
  return { list };
})(LocationMapShow);
